#21500630 전여훈#20181204#tkinter 로 만들어진 GUI에 입력받은 문자열이 회문인지 검사하여 참인지 여부를 보여주는 프로그램.from tkinter import *from tkinter import ttkdef calculate(*args):    try:        newStr = []        value = str_entry.get()        userStr = list(value)        for i in range (len(userStr)):            newStr.append(userStr[len(userStr)-1-i])        if newStr == userStr:            result.set("True")        else:            result.set("False")                except ValueError:        passroot = Tk()root.title("Is the string Palindrome?") mainframe = ttk.Frame(root, padding="3 3 12 12")mainframe.grid(column=0, row=0, sticky = (N, W, E, S))mainframe.columnconfigure(0, weight=1)mainframe.rowconfigure(0, weight=1)string=StringVar()result=StringVar()str_entry = ttk.Entry(mainframe, width=25, textvariable=string)str_entry.grid(column=2, row=1, sticky=(W, E))ttk.Label(mainframe, text = "Input String").grid(column=1, row=1, sticky=W)ttk.Label(mainframe, textvariable = result).grid(column=2, row=3, sticky=N)ttk.Button(mainframe, text = "Check Palindrome", command=calculate). grid(column=2, row=2, sticky=N)